
var staffs={init:function(){$j('.jstaff_username').keyup(function(){staffs.get_staff_list();});},bind:function(){$j('.juas_list').find('li').unbind('click');$j('.juas_list').find('li').bind('click',function(){$j('.jstaff_username').val($j(this).attr('title'));$j("input[name='staffs_id']").val($j(this).attr('rel'));$j('.juas_list').css('display','none');});},get_staff_list:function(){$j('.juas_list').css('display','block');var search=$j('.jstaff_username').val();$j.ajax({type:'post',url:base_url+'staffs/get_staff_list/',data:{search:search},success:function(html){$j('.juas_list').html(html);staffs.bind();},error:function(a,b,c){alert(a+'\n'+b+'\n'+c);}});}}